        $user->name = $this->faker->name;
        $user->email = $this->faker->unique()->email;
        $user->password = hash::make('123456');
        $user->avatar = array_rand($avatars);
        $user->save();

        for($i = 0; $i < 3; $i++){
            $this->userHasInterest($user->id);
        }

        for($i = 0; $i < 5; $i++){
            $this->userHasFriend($user->id);
